		 , MI_MEMO		, CRE_ID		, CRE_DT		, UPD_ID		, UPD_DT
	)VALUES(
		   #{mbrNo}		, #{fstDrugCd}	, #{fstDrug}	, #{mainDrugCd}	, #{mainDrug}
		 , #{fstAge}	, #{useTerm}	, #{useFrqCd}	, #{useCauCd}	, #{useCauEtc}
