mapper.select:

	SELECT /* cslAssEvl.getCslAssEvlInfo */
		   MBR_NO		, EVL_SEQ		, EVL_TOL_CD		, EVL_SCO		, EVL_CTNT
	  FROM CSL_ASS_EVL
	 WHERE MBR_NO = #{mbrNO}
	   AND EVL_SEQ = #{evlSeq}


	SELECT /* cslAssEvl.getEvlSqeNext */ IFNULL(MAX(EVL_SEQ), 0) + 1
	  FROM CSL_ASS_EVL
	 WHERE MBR_NO = #{mbrNo}

mapper.insert:

	INSERT /* cslAssEvl.insertCslAssEvl */ INTO CSL_ASS_EVL(
		MBR_NO		, EVL_SEQ		, EVL_TOL_CD		, EVL_SCO		, EVL_CTNT
	)VALUES(
		#{mbrNo}	, #{evlSeq}		, #{evlTolCd}		, #{evlSco}		, #{evlCtnt}
	)

mapper.delete:

	DELETE /* cslAssEvl.deleteCslAssEvl */ CSL_ASS_EVL
	 WHERE MBR_NO = #{mbrNo}
	   AND EVL_SEQ = #{evlSeq}
