mapper.insert:

	INSERT /* cslRcp.insertCslRcp */ INTO CSL_RCP(
		   RCP_NO							, CSL_DT						, CSL_FM_TM						, CSL_TO_TM			, CSL_TERM_TM
		 , CSL_ID							, CSL_NM						, IFP_GB_CD						, IFP_GB_ETC		, IFP_MBR_NO
		 , IFP_NM							, IFP_GEND_CD					, IFP_AGE						, IFP_TEL_NO1		, IFP_TEL_NO2
		 , IFP_TEL_NO3						, IFP_AREA_CD					, IFP_JOB_CD					, TGP_MBR_NO		, TGP_NM
		 , TGP_GEND_CD						, TGP_AGE						, TGP_TEL_NO1					, TGP_TEL_NO2		, TGP_TEL_NO3
		 , TGP_AREA_CD						, TGP_FRG_CD					, TGP_JOB_CD					, IF_PATH_CD		, PBM_KND_CD
		 , CSL_TP_CD						, CSL_HIS_CD					, FST_DRUG_CD					, MAIN_DRUG_CD		, MAIN_DRUG
		 , CSL_CTNT							, MJR_MNG_CD					, AST_SCO						, RSKA_TP_CD		, RSKB_TP_CD
		 , RSKC_TP_CD						, RSK_SCO						, CRE_ID						, CRE_DT			, UPD_ID
		 , UPD_DT							, IFP_AREA_ETC					, TGP_AREA_ETC
	)VALUES(
		   #{rcpNo}							, #{cslDt}						, #{cslFmTm}					, #{cslToTm}		, CAST(#{cslTermTm} AS UNSIGNED)
		 , #{cslId}							, #{cslNm}						, #{ifpGbCd}					, #{ifpGbEtc}		, #{ifpMbrNo}
		 , #{ifpNm}							, #{ifpGendCd}					, #{ifpAge}						, #{ifpTelNo1}		, #{ifpTelNo2}
		 , CAST(#{ifpTelNo3} AS UNSIGNED)	, #{ifpAreaCd}					, #{ifpJobCd}					, #{tgpMbrNo}		, #{tgpNm}
		 , #{tgpGendCd}						, #{tgpAge}						, #{tgpTelNo1}					, #{tgpTelNo2}		, #{tgpTelNo3}
		 , #{tgpAreaCd}						, #{tgpFrgCd}					, #{tgpJobCd}					, #{ifPathCd}		, #{pbmKndCd}
		 , #{cslTpCd}						, #{cslHisCd}					, #{fstDrugCd}					, #{mainDrugCd}		, #{mainDrug}
		 , #{cslCtnt}						, #{mjrMngCd}					, CAST(#{astSco} AS UNSIGNED)	, #{rskaTpCd}		, #{rskbTpCd}
		 , #{rskcTpCd}						, CAST(#{rskSco} AS UNSIGNED)	, #{cslId}						, NOW()				, #{cslId}
		 , NOW()							, #{ifpAreaEtc}					, #{tgpAreaEtc}
	)

mapper.select:
SELECT /* cslRcp.getCslRcpList */ * FROM ( SELECT T.*, @RNUM := @RNUM + 1 AS ROWNUM FROM ( SELECT RCP_NO , CSL_DT , CSL_FM_TM , CSL_TO_TM , CSL_TERM_TM , CSL_NM , IFP_NM , TGP_NM , CRE_DT FROM CSL_RCP WHERE CSL_DT BETWEEN #{schStrCslDt} AND #{schEndCslDt}
AND CSL_NM LIKE CONCAT('%', REPLACE(#{schNm}, ' ', ''), '%')
AND IFP_NM LIKE CONCAT('%', REPLACE(#{schNm}, ' ', ''), '%')
AND TGP_NM LIKE CONCAT('%', REPLACE(#{schNm}, ' ', ''), '%')
AND ( CSL_NM LIKE CONCAT('%', REPLACE(#{schNm}, ' ', ''), '%') OR IFP_NM LIKE CONCAT('%', REPLACE(#{schNm}, ' ', ''), '%') OR TGP_NM LIKE CONCAT('%', REPLACE(#{schNm}, ' ', ''), '%') )
ORDER BY CRE_DT DESC ) T,(SELECT @RNUM := 0) RNUM ORDER BY T.CRE_DT DESC ) T1 WHERE ROWNUM > (#{currentPageNo}-1) * #{recordCountPerPage} ORDER BY T1.CRE_DT DESC LIMIT #{recordCountPerPage}

mapper.delete:

	DELETE /* cslRcp.deleteCslRcp */
	  FROM CSL_RCP
	  WHERE RCP_NO = #{rcpNo}

mapper.update:

	UPDATE /* cslRcp.updateCslRcp */ CSL_RCP
	   SET CSL_DT			= #{cslDt}							, CSL_FM_TM			= #{cslFmTm}
		 , CSL_TO_TM		= #{cslToTm}						, CSL_TERM_TM		= CAST(#{cslTermTm} AS UNSIGNED)
		 , CSL_ID			= #{cslId}							, CSL_NM			= #{cslNm}
		 , IFP_GB_CD		= #{ifpGbCd}						, IFP_GB_ETC		= #{ifpGbEtc}
		 , IFP_MBR_NO		= #{ifpMbrNo}						, IFP_NM			= #{ifpNm}
		 , IFP_GEND_CD		= #{ifpGendCd}						, IFP_AGE			= #{ifpAge}
		 , IFP_TEL_NO1		= #{ifpTelNo1}						, IFP_TEL_NO2		= #{ifpTelNo2}
		 , IFP_TEL_NO3		= CAST(#{ifpTelNo3} AS UNSIGNED)	, IFP_AREA_CD		= #{ifpAreaCd}
		 , IFP_JOB_CD		= #{ifpJobCd}						, TGP_MBR_NO		= #{tgpMbrNo}
		 , TGP_NM			= #{tgpNm}							, TGP_GEND_CD		= #{tgpGendCd}
		 , TGP_AGE			= #{tgpAge}							, TGP_TEL_NO1		= #{tgpTelNo1}
		 , TGP_TEL_NO2		= #{tgpTelNo2}						, TGP_TEL_NO3		= #{tgpTelNo3}
		 , TGP_AREA_CD		= #{tgpAreaCd}						, TGP_FRG_CD		= #{tgpFrgCd}
		 , TGP_JOB_CD		= #{tgpJobCd}						, IF_PATH_CD		= #{ifPathCd}
		 , PBM_KND_CD		= #{pbmKndCd}						, CSL_TP_CD			= #{cslTpCd}
		 , CSL_HIS_CD		= #{cslHisCd}						, FST_DRUG_CD		= #{fstDrugCd}
		 , MAIN_DRUG_CD		= #{mainDrugCd}						, MAIN_DRUG			= #{mainDrug}
		 , CSL_CTNT			= #{cslCtnt}						, MJR_MNG_CD		= #{mjrMngCd}
		 , AST_SCO			= CAST(#{astSco} AS UNSIGNED)		, RSKA_TP_CD		= #{rskaTpCd}
		 , RSKB_TP_CD		= #{rskbTpCd}						, RSKC_TP_CD		= #{rskcTpCd}
		 , RSK_SCO			= CAST(#{rskSco} AS UNSIGNED)		, UPD_ID			= #{cslId}
		 , UPD_DT			= NOW()								, IFP_AREA_ETC		= #{ifpAreaEtc}
		 , TGP_AREA_ETC		= #{tgpAreaEtc}
	 WHERE RCP_NO = #{rcpNo}
