mapper.select:

	SELECT /* sysCd.getSysCdList */
		   CD_ID, CD_NM, DP_SEQ
	  FROM SYS_CD

	
		
	   AND GRP_CD = #{grpCd}
		
		
	   AND USE_YN = #{useYn}

mapper.insert:

	INSERT /* sysCd.insertSysCd */ INTO SYS_CD(
		GRP_CD		, CD_ID			, CD_NM			, DP_SEQ		, USE_YN
	  , CRE_DT		, CRE_ID		, UPD_DT		, UPD_ID
	)VALUE(
		#{grpCd}	, #{cdId}		, #{cdNm}		, #{dpSeq}		, #{useYn}
	  , #{creDt}	, #{creId}		, #{updDt}		, #{updId}
	)
