mapper.select:
/* edPrg.selectEdPrmList */ SELECT @RNUM := @RNUM + 1 AS ROWNUM , PGM_ID , PGM_ED_CD , (SELECT Z.CD_NM FROM SYS_CD Z WHERE Z.CD_ID =PGM_ED_CD) AS PGM_ED_NM , PGM_CLASS_NM_CD , (SELECT Z.CD_NM FROM SYS_CD Z WHERE Z.CD_ID =PGM_CLASS_NM_CD) AS PGM_CLASS_NM , PGM_CLASS_SUB_CD , (SELECT Z.CD_NM FROM SYS_CD Z WHERE Z.CD_ID =PGM_CLASS_SUB_CD) AS PGM_CLASS_SUB_NM , (SELECT COUNT(Z.PGM_MBR_ID) FROM ED_PGM_MBR Z WHERE Z.PGM_ID = EP.PGM_ID) AS PGM_MBR_COUNT , PGM_MAIN_LEC , PGM_SUB_LEC , PGM_START_DT , PGM_END_DT , PGM_DUR , PGM_SESSION , PGM_CLASS , PGM_AGENT , PGM_MNG_USR_ID , PGM_CLASS_START_DT , PGM_CLASS_END_DT , PGM_CLASS_START_TM , PGM_CLASS_END_TM , PGM_CLASS_DUR , PGM_SUBJECT , PGM_GOAL , PGM_CTNT , PGM_RST , FILE_ID , PGM_EMP , PGM_VOL , CRE_ID , CRE_DT , UPD_ID , UPD_DT FROM ED_PGM, (SELECT @RNUM := 0) RNUM WHERE 1 = 1
AND PGM_START_DT >= #{schStrDt}
AND PGM_END_DT <= #{schEndDt}
AND PGM_ED_CD IN
#{item}
AND PGM_CLASS_NM_CD IN
#{item}
AND PGM_CLASS_SUB_CD IN
#{item}

